import axios from 'axios';
import React, { useState } from 'react';

export function Nuevo() {
    const [nombre, setNombre] = useState("");
    const [usuario, setUsuario] = useState("");
    const [password, setPassword] = useState("");
    const [foto, setFoto] = useState("");

    async function guardarDatos(e) {
        e.preventDefault();
        const formData = new FormData();
        formData.append("nombre", nombre);
        formData.append("usuario", usuario);
        formData.append("password", password);
        formData.append("foto", foto);
        
        try {
            const res = await axios.post("http://localhost:3000/api/nuevousuario", formData, {
                headers: {
                    'Content-Type': "multipart/form-data"
                }
            });
            console.log(res);
        } catch (error) {
            console.error('Error al guardar usuario:', error);
        }
    }

    return (
        <div className="container">
            <form onSubmit={guardarDatos}>
                <div className="card">
                    <div className="card-header"></div>
                    <h1>Nuevo usuario</h1>
                    <div className="card-body">
                        <input className="form-control mb-3" type="text" name="nombre" id="nombre" value={nombre} onChange={(e)=>setNombre(e.target.value)} autoFocus />
                        <input className="form-control mb-3" type="text" name="usuario" id="usuario" value={usuario} onChange={(e)=>setUsuario(e.target.value)} autoFocus />
                        <input className="form-control mb-3" type="text" name="password" id="password" value={password} onChange={(e)=>setPassword(e.target.value)} autoFocus /> 
                        <input className="form-control mb-3" type="file" name="foto" id="foto" onChange={(e)=>setFoto(e.target.files[0])} autoFocus />
                    </div>
                    <div className="card-footer">
                        <button className="form-control btn btn-primary" type="submit">Guardar usuario</button>
                    </div>
                </div>
            </form>
        </div>
    );
}
