import e from 'express';
import React, { useState } from 'react';

export function Nuevo() {
    const [nombre, setNombre] = useState("");
    const [usuario, setUsuario] = useState("");
    const [password, setPassword] = useState("");
    const [foto, setFoto] = useState("");

    function guardarDatos(e) {
        e.preventDefault();
        const formData=new FormData();
        formData.append("nombre",nombre);
        formData.append("usuario", usuario);
        formData.append("password", password);
       
            formData.append("foto", foto.files[0]);
    }

    return (
        <div className="container">
            <form onSubmit={guardarDatos}>
                <div className="card">
                    <div className="card-header"></div>
                    <h1>Nuevo usuario</h1>
                    <div className="card-body">
                        <input className="form-control mb-3" type="text" name="nombre" id="nombre" onChange={(e)=>setNombre(e.target.value)}autoFocus />
                        <input className="form-control mb-3" type="text" name="usuario" id="usuario" onChange={(e)=>setUsuario(e.target.value)}autoFocus />
                        <input className="form-control mb-3" type="text" name="password" id="password" onChange={(e)=>setPassword(e.target.value)}autoFocus /> 
                        <input className="form-control mb-3" type="file" name="foto" id="foto" onChange={(e)=>setFoto(e.target.file)}autoFocus />
                    </div>
                    <div className="card-footer">
                        <button className="form-control btn btn-primary" type="submit">Guardar usuario</button>
                    </div>
                </div>
            </form>
        </div>
    );
}
